import Router from 'next/router';
import React, { useState } from 'react';

const authReducer = async (state, action) => {
  const [open, setSnackOpen] = useState(false);
  switch (action.type) {
    case 'LOGIN':
      // setLoader(true);
      // getToken();

      var data = {
        email: action.email,
        password: action.password
      };
      try {
        const proxyUrl = 'https://cors-anywhere.herokuapp.com/';
        const url = 'https://owusu-task-manager.herokuapp.com/users/login';
        const response = await fetch(proxyUrl + url, {
          method: 'POST',
          body: JSON.stringify(data),
          headers: {
            'Content-Type': 'application/json'
          }
        });

        const userInfo = await response.json();
        var token = userInfo.token;
        state.token = token;
        Router.push('/TodoAppPage');
        //   setLoader(false);
        // this.setState(() => ({ loading: false, token: userInfo.token }));

        // Router.push('/TodoAppPage');
      } catch (error) {
        console.error('Error:', error);
        Router.push('/');
        //   setLoader(false);
        setSnackOpen(true);

        // this.setState({ email: '', password: '' });
      }
      return state;

    //     return [...state, { id: uuid(), task: action.task, completed: false }];
    //   case 'LOGOUT':
    //     return state.filter(todo => todo.id !== action.id);
    //   case 'TOGGLE':
    //     return state.map(todo =>
    //       todo.id === action.id ? { ...todo, completed: !todo.completed } : todo
    //     );
    //   case 'EDIT':
    //     return state.map(todo =>
    //       todo.id === action.id ? { ...todo, task: action.newTask } : todo
    //     );
    default:
      console.log('case not found in reducer');
      return state;
  }
};
export default authReducer;

