import { useReducer, useEffect } from 'react';

//token , starter tasks?, reducer
function useAuthReducer(authReducer, token) {
  //make piece of state, based off of value in localstorage (or default)
  //in case of a DB the key will equal the collection name
  const [state, authenticate] = useReducer(authReducer, token);

  //   useEffect(() => {
  //     window.localStorage.setItem(key, JSON.stringify(state)); //use for post request (i.e update/delete/add tasks)
  //   }, [state, key]);

  return [state, authenticate];
}

export { useAuthReducer };
