  constructor(props) {
    super(props);
  }

  static async getInitialProps() {
    const res = await axios.get('https://jsonplaceholder.typicode.com/posts');
    const { data } = res; // const data = res.data
    return { posts: data }; // res.data is now saved to props.posts
  }

  render() {
