import React, { useContext, useEffect, useState, Component } from "react";
import Paper from "@material-ui/core/Paper";
import List from "@material-ui/core/List";
import Divider from "@material-ui/core/Divider";
import DraggableTodoList from "./DraggableTodoList";
import { TodosContext } from "../components/contexts/todos.context";
import { TodosProvider } from "../components/contexts/todos.context";

class TodoList extends Component {
  static contextType = TodosContext;

  constructor(props) {
    super(props);
    this.state = {
      tasks: props.posts,
      sorted: false
    };
    this.onSortEnd = this.onSortEnd.bind(this);
  }

  onSortEnd = ({ oldIndex, newIndex }) => {
    console.log("before sort: ", this.props.posts);
    let arr = this.props.posts;
    arr = arr.slice();
    arr.splice(
      newIndex < 0 ? arr.length + newIndex : newIndex,
      0,
      arr.splice(oldIndex, 1)[0]
    );
    console.log("after sort: ", arr);
    this.setState({
      tasks: arr,
      sorted: true
    });
    console.log("this.state.tasks: ", this.state.tasks);
    // return arr;
  };

  // console.log(this.state);
  // this.setState({
  //   tasks: arrayMove(this.state, oldIndex, newIndex)
  // });
  // // this.setState({
  // //   tasks: arrayMove([4, 5, 6, 7, 8, 0], oldIndex, newIndex)
  // // });

  // componentDidUpdate(prevProps) {
  //   if (this.props.posts !== prevProps.posts)
  //     this.setState({
  //       tasks: this.context
  //     });
  // }
  //***activate whenever delete,add,edit,toggle */
  // ***do not run after sorting
  static getDerivedStateFromProps(props, state) {
    if (state.sorted) {
      //check if props length is different from state length [add/delete]
      if (props.posts.length !== state.tasks.length) {
        // console.log("1st condition met");
        //update state to match props
        return { tasks: props.posts };
        //check if state length is equal to posts length
      } /*else if (props.posts.length === state.tasks.length) {*/
      // console.log("2nd condition met");
      //loop through each task in state
      for (let i = 0; i < state.tasks.length; i++) {
        //if props tasks equal state tasks completion, continue to next element
        if (props.posts[i].completed === !state.tasks[i].completed) {
          //if there is inequality of props vs. state completion, update state to match props
          return { tasks: props.posts };
        }
        if (props.posts[i].description !== state.tasks[i].description) {
          return { tasks: props.posts };
        }
      }
      /*} else {*/
      return null;
    } else return null;
  }

  //don't update the state if length, completed status haven't been changed else update

  //check if completed is changed

  // componentWillReceiveProps(nextProps) {
  //   this.setState({
  //     tasks: nextProps.posts
  //   });
  // }

  render() {
    console.log("todo list rendered!");
    // console.log(this.context);
    const { posts } = this.props;
    const { tasks } = this.state;
    if (tasks.length)
      return (
        <DraggableTodoList
          axis="y"
          distance={20}
          onSortEnd={this.onSortEnd}
          posts={tasks}
        ></DraggableTodoList>
      );
    return null;
  }
}

export default TodoList;

