import React, { useContext, useEffect } from "react";
import Router from "next/router";
import Avatar from "@material-ui/core/Avatar";
import Button from "@material-ui/core/Button";
import FormControl from "@material-ui/core/FormControl";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";
import Input from "@material-ui/core/Input";
import InputLabel from "@material-ui/core/InputLabel";
import LockOutlinedIcon from "@material-ui/icons/LockOutlined";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import MenuItem from "@material-ui/core/MenuItem";
import Select from "@material-ui/core/Select";
import Snackbar from "@material-ui/core/Snackbar";
import IconButton from "@material-ui/core/IconButton";
import CloseIcon from "@material-ui/icons/Close";
import withStyles from "@material-ui/core/styles/withStyles";
import styles from "./styles/FormStyles";
import { LanguageContext } from "./contexts/LanguageContext";
import { ThemeContext } from "./contexts/ThemeContext";
import { UserContext, AuthenticateContext } from "./contexts/user.context";
import { TodosContext } from "./contexts/todos.context";
import useInputState from "./hooks/useInputState";
import { Cookies } from "react-cookie";
import cubeSpinner from "./styles/cubeSpinner";
import { SnackbarProvider, useSnackbar } from "notistack";

const words = {
  english: {
    signIn: "Sign In",
    email: "Email Address",
    password: "Password",
    remember: "Remember Me",
    errorMessage: "Email and/or Password incorrect",
    demoMessage:
      "Welcome to Task App Demo! Email: taskappdemo@demo.com | Password: 1234567"
  },
  french: {
    signIn: "Se Connecter",
    email: "Adresse Électronique",
    password: "Mot de Passe",
    remember: "Souviens-toi De Moi",
    errorMessage: "E-mail et / ou mot de passe incorrect",
    demoMessage:
      "Welcome to Task App Demo. Email: taskappdemo@demo.com | Password: 1234567"
  },
  spanish: {
    signIn: "Registrarse",
    email: "Correo Electrónico",
    password: "Contraseña",
    remember: "Recuérdame",
    errorMessage: "Correo electrónico y / o contraseña incorrectos",
    demoMessage:
      "Welcome to Task App Demo. Email: taskappdemo@demo.com | Password: 1234567"
  },
  german: {
    signIn: "anmelden",
    email: "E-Mail-Addresse",
    password: "Passwort",
    remember: "Behalte mich in Erinnerung",
    errorMessage: "E-Mail und / oder Passwort falsch",
    demoMessage:
      "Welcome to Task App Demo. Email: taskappdemo@demo.com | Password: 1234567"
  },

  日本語: {
    signIn: "サインイン",
    email: "電子メールアドレス",
    password: "パスワード",
    remember: "私を覚えている",
    errorMessage: "メールアドレスまたはパスワードが正しくありません",
    demoMessage:
      "Welcome to Task App Demo. Email: taskappdemo@demo.com | Password: 1234567"
  }
};

const cookies = new Cookies();
function Form(props) {
  const { language, changeLanguage } = useContext(LanguageContext);
  const token = useContext(UserContext);
  const authenticate = useContext(AuthenticateContext);
  const { classes } = props;
  const {
    email,
    signIn,
    password,
    remember,
    errorMessage,
    demoMessage
  } = words[language];
  const [emailVal, handleEmailChange, emailReset] = useInputState("");
  const [passVal, handlePassChange, passReset] = useInputState("");
  // const [open, setSnackOpen] = React.useState(false);
  const [loading, setLoader] = React.useState(false);
  const { enqueueSnackbar, closeSnackbar } = useSnackbar();

  const handleErrorMessage = () => {
    const message = errorMessage;
    enqueueSnackbar(message, {
      variant: "error"
    });
  };

  const handleDemoMessage = () => {
    const message = demoMessage;

    enqueueSnackbar(message, {
      onEnter: closeSnackbar(),
      preventDuplicate: true,
      variant: "info",
      anchorOrigin: {
        vertical: "bottom",
        horizontal: "left"
      },
      persist: true
    });
  };

  // const handleClick = () => {
  //   setSnackOpen(true);
  // };

  // const handleClose = (event, reason) => {
  //   if (reason === "clickaway") {
  //     return;
  //   }

  //   setSnackOpen(false);
  // };

  const handleSubmit = e => {
    e.preventDefault();
    setLoader(true);
    authenticate({ type: "LOGIN", email: emailVal, password: passVal });
    console.log("submitted!");
    setTimeout(() => {
      if (token.error == true) {
        console.log("setting loading to true");
        setLoader(false);
        handleErrorMessage();
      }
    }, 2000);
    emailReset();
    passReset();
  };

  useEffect(() => {
    handleDemoMessage();
  }, [language]);

  if (loading) {
    return <div className={classes.loader}>{cubeSpinner()}</div>;
  }

  return (
    <main className={classes.main}>
      <Paper elevation={3} className={classes.paper}>
        <Avatar className={classes.avatar}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography variant="h5">{signIn}</Typography>
        <Select value={language} onChange={changeLanguage}>
          <MenuItem value="english">English</MenuItem>
          <MenuItem value="french">French</MenuItem>
          <MenuItem value="spanish">Spanish</MenuItem>
          <MenuItem value="german">German</MenuItem>
          <MenuItem value="日本語">日本語</MenuItem>
        </Select>
        {/* /*add the snackbar init here*/}
        <form className={classes.form} onSubmit={handleSubmit}>
          <FormControl margin="normal" required fullWidth>
            <InputLabel htmlFor="email">{email}</InputLabel>
            <Input
              id="email"
              name="email"
              value={emailVal}
              onChange={handleEmailChange}
              autoFocus
            ></Input>
          </FormControl>
          <FormControl margin="normal" required fullWidth>
            <InputLabel htmlFor="password">{password}</InputLabel>
            <Input
              id="password"
              name="password"
              value={passVal}
              onChange={handlePassChange}
              autoFocus
            ></Input>
          </FormControl>
          <FormControlLabel
            control={<Checkbox color="primary" />}
            label={remember}
          />
          <Button
            variant="contained"
            type="submit"
            fullWidth
            color="primary"
            className={classes.submit}
          >
            {signIn}
          </Button>
        </form>
      </Paper>
    </main>
  );
}

export default withStyles(styles)(Form);
