import React, { Component } from "react";
import DraggableTodoList from "./DraggableTodoList";
import { withSortedContext } from "./contexts/SortedContext";

class TodoList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tasks: props.posts,
      prevArr: [],
      sorted: false
    };
  }

  onSortEnd = ({ oldIndex, newIndex }) => {
    console.log("before sort: ", this.state.prevArr);
    if (this.state.prevArr.length === 0) {
      var arr = this.props.posts;
      //if prevArr is same size as state.tasks change nothing
    } else if (this.state.prevArr.length === this.state.tasks.length) {
      var arr = this.state.prevArr; //when a new task is added ..it's not reflected in prevArr...duh!

      //if prev arr is smaller than state.tasks push the new element to the end
    } else if (this.state.prevArr.length < this.state.tasks.length) {
      this.setState({
        prevArr: [
          ...this.state.prevArr,
          this.state.tasks[this.state.tasks.length - 1]
        ]
      });
      var arr = this.state.prevArr;

      //if prev arr is larger than state.tasks filter out the corresponding element
    } else if (this.state.prevArr.length > this.state.tasks.length) {
      this.setState({
        prevArr: this.state.prevArr.filter(
          task => task.id !== this.props.sortedContext._id
        )
      });
      var arr = this.state.prevArr;
    } else {
      return console.log("something did not go quite as planned...");
    }

    arr = arr.slice();
    arr.splice(
      newIndex < 0 ? arr.length + newIndex : newIndex,
      0,
      arr.splice(oldIndex, 1)[0]
    );
    console.log("after sort: ", arr);
    this.setState({
      tasks: arr,
      prevArr: arr,
      sorted: true
    });
  };

  static getDerivedStateFromProps(props, state) {
    //consider component did update
    if (!state.sorted || props.sortedContext.isSorted) {
      if (props.posts.length !== state.tasks.length) {
        return { tasks: props.posts };
      }
      for (let i = 0; i < state.tasks.length; i++) {
        if (props.posts[i].completed === !state.tasks[i].completed) {
          return { tasks: props.posts };
        }
        if (props.posts[i].description !== state.tasks[i].description) {
          return { tasks: props.posts };
        }
      }
      return null;
    } else return null;
  }

  render() {
    console.log("this.state.sorted: ", this.state.sorted);
    const { posts, resetSort } = this.props.sortedContext;
    const { tasks } = this.state;
    if (tasks.length)
      return (
        <DraggableTodoList
          axis="y"
          distance={20}
          onSortEnd={this.onSortEnd}
          posts={tasks}
          sorted={resetSort}
        ></DraggableTodoList>
      );
    return null;
  }
}

export default withSortedContext(TodoList);
