import React, { useContext, memo } from "react";
import TodoList from "../components/TodoList";
import TodoForm from "../components/TodoForm";
// import Typography from '@material-ui/core/Typography';
import Paper from "@material-ui/core/Paper";
import { handleAuthSSR } from "../components/handleAuthSSR";
import Grid from "@material-ui/core/Grid";
import { ThemeContext } from "../components/contexts/ThemeContext";

import { TodosProvider } from "../components/contexts/todos.context";
import { TodosContext } from "../components/contexts/todos.context";

function TodoAppPage(props) {
  const todos = useContext(TodosContext);
  const { isDarkMode, toggleTheme } = useContext(ThemeContext);
  // console.log("todos in todoapppage sent from todoscontext: ", todos);

  return (
    <Paper
      style={{
        paddding: 0,
        margin: 0,
        height: "100vh",
        backgroundColor: isDarkMode ? "#394bad" : "#edeef2"
      }}
      elevation={0}
    >
      <Grid container justify="flex-start" style={{ marginTop: "1rem" }}>
        <Grid item xs={11} md={8} lg={4}>
          <TodosProvider>
            <TodoForm />
            <TodoList {...props} />
          </TodosProvider>
        </Grid>
      </Grid>
    </Paper>
  );
}

TodoAppPage.getInitialProps = async ctx => {
  // console.log("ping!");
  const res = await handleAuthSSR(ctx);
  const { data } = res; // const data = res.data

  return {
    posts: data
  };
};

export default TodoAppPage;
