import { useReducer, useEffect } from "react";

function useDBReducer(defaultVal, todoReducer) {
  const [state, dispatch] = useReducer(todoReducer, defaultVal, {});

  //maybe use effect here??
  console.log("state after the reducer:", state);
  return [state, dispatch];
}

export { useDBReducer };
