import uuid from "uuid/v4";
import axios from "axios";
import Router from "next/router";

import { Cookies } from "react-cookie";

const cookies = new Cookies();
const token = cookies.get("token");

const todoReducer = async (state, action) => {
  console.log("state before switch/case executes: ", state);
  switch (action.type) {
    case "ADD":
      const description = action.task;
      const body = { description };
      try {
        const proxyUrl = "https://cors-anywhere.herokuapp.com/";
        const targetUrl = "https://owusu-task-manager.herokuapp.com/tasks";
        const res = await axios({
          url: proxyUrl + targetUrl,
          method: "post",
          data: body,
          headers: {
            Authorization: "Bearer " + token
          }
        });
        const newTask = res.data;

        console.log("state after switch/case executes: ", state);

        return [...state, { id: uuid(), task: action.task, completed: false }];
        break;
      } catch (e) {
        console.log(e);
      }

    case "REMOVE":
      console.log("remove clicked!");
      try {
        const proxyUrl = "https://cors-anywhere.herokuapp.com/";
        const targetUrl = `https://owusu-task-manager.herokuapp.com/tasks/${action.id}`;
        const res = await axios({
          url: proxyUrl + targetUrl,
          method: "delete",
          headers: {
            Authorization: "Bearer " + token
          }
        });
        Router.push("/TodoAppPage");
        console.log("removing task from state: ", state);
        // return state.filter(todo => todo._id !== action._id);

        break;
      } catch (e) {
        console.log(e);
      }
    case "TOGGLE":
      console.log("toggle clicked!");
      try {
        const proxyUrl = "https://cors-anywhere.herokuapp.com/";
        const targetUrl = `https://owusu-task-manager.herokuapp.com/tasks/${action.id}`;
        const isDone = !action.completed;
        const updateData = { completed: isDone };
        const res = await axios({
          url: proxyUrl + targetUrl,
          method: "patch",
          data: updateData,
          headers: {
            Authorization: "Bearer " + token
          }
        });
        Router.push("/TodoAppPage");
        break;
      } catch (e) {
        console.log(e);
      }

    case "EDIT":
      try {
        const proxyUrl = "https://cors-anywhere.herokuapp.com/";
        const targetUrl = `https://owusu-task-manager.herokuapp.com/tasks/${action.id}`;
        const description = action.newTask;
        const body = { description };
        const res = await axios({
          url: proxyUrl + targetUrl,
          method: "patch",
          data: body,
          headers: {
            Authorization: "Bearer " + token
          }
        });
        Router.push("/TodoAppPage");
        break;
      } catch (e) {
        console.log(e);
      }

    default:
      return state;
  }
};

export default todoReducer;
