import React, { useContext } from "react";
import { ThemeContext } from "./contexts/ThemeContext";
import formDark from "./styles/formDark.svg";

export default function PageContent(props) {
  const { isDarkMode } = useContext(ThemeContext);

  const styles = {
    backgroundColor: isDarkMode ? "red" : "white",
    // backgroundImage: isDarkMode ? `"url(${formDark})"` : "white",
    height: "100vh",
    width: "100vw"
  };

  <style jsx>{`
    .svg {
        width:100%;
        height:100%;
        position:absolute;
        background: url('${formDark}') no-repeat;
    }
`}</style>;

  return (
    <div className="svg" style={styles}>
      {props.children}
    </div>
  );
}
