import { useReducer, useEffect } from "react";

function useDBReducer(key, defaultVal, todoReducer) {
  const [state, dispatch] = useReducer(
    todoReducer,
    defaultVal /*() => {
    let val;
    try {
      val = JSON.parse(window.localStorage.getItem(key) || "[]");
      console.log("successfully stored val: ", val);
    } catch (e) {
      val = defaultVal;
    }
    return val;
  }*/
  );

  // useEffect(() => {
  //   window.localStorage.setItem(key, JSON.stringify(state)); //use for post request (i.e update/delete/add tasks)
  //   console.log("state changed! setting local storage: ", state);
  // }, [state]);

  return [state, dispatch];
}

export { useDBReducer };
