import { useReducer, useEffect } from 'react';
import axios from 'axios';
function useDBReducer(todoReducer, defaultVal, token) {
  const [state, dispatch] = useReducer(todoReducer, defaultVal, () => {
    var val;

    const proxyUrl = 'https://cors-anywhere.herokuapp.com/';
    const targetUrl = 'https://owusu-task-manager.herokuapp.com/tasks';

    try {
      axios
        .get(proxyUrl + targetUrl, {
          headers: {
            Authorization: 'Bearer ' + token.token //the token is a variable which holds the token
          }
        })
        .then(response => {
          console.log(response.data);
          val = response.data;
        })
        .catch(e => {
          console.log(e);
        });
    } catch (e) {
      console.error('Error:', e);
      val = defaultVal;
    }
    return val;
  });
  console.log('test', state);
  console.log('test2', dispatch);
  return [state, dispatch];
}

export { useDBReducer };

