import React, { useContext, memo } from "react";
import useToggleState from "./hooks/useToggleState";
import EditTodoForm from "./EditTodoForm";
import ListItemText from "@material-ui/core/ListItemText";
import ListItem from "@material-ui/core/ListItem";
import Checkbox from "@material-ui/core/Checkbox";
import IconButton from "@material-ui/core/IconButton";
import DeleteIcon from "@material-ui/icons/Delete";
import EditIcon from "@material-ui/icons/Edit";
import { SnackbarProvider, useSnackbar } from "notistack";
import ListItemSecondaryAction from "@material-ui/core/ListItemSecondaryAction";
import { DispatchContext } from "./contexts/todos.context";
import { LanguageContext } from "./contexts/LanguageContext";
import { Typography } from "@material-ui/core";
import moment from "moment";

const words = {
  en: {
    addTodo: "Add New Todo",
    lastUpdated: "Last Update: "
  },
  fr: {
    addTodo: "Ajouter un nouveau Todo",
    lastUpdated: "Dernières mises à jour: "
  },
  es: {
    addTodo: "Añadir nuevo Todo",
    lastUpdated: "Últimas actualizaciones: "
  },
  de: {
    addTodo: "Neuen Todo hinzufügen",
    lastUpdated: "Letzte Aktualisierung: "
  },

  ja: { addTodo: "新しいTodoを追加", lastUpdated: "最新の更新: " }
};

function Todo({ _id, description, completed, updatedAt }) {
  const dispatch = useContext(DispatchContext);
  const { language } = useContext(LanguageContext);
  const [isEditing, toggle] = useToggleState(false);
  const { enqueueSnackbar, closeSnackbar } = useSnackbar();
  const { lastUpdated, addTodo } = words[language];

  const handleDeleteClick = () => {
    enqueueSnackbar(`Deleted Task: "${description}"`);
  };

  const handleEditClick = () => {
    enqueueSnackbar(`Edited Task: "${description}"`);
  };

  const formatedDate = () => {
    moment.locale(language);
    return moment(updatedAt).format("lll");
  };

  return (
    <ListItem style={{ height: "65px" }}>
      {isEditing ? (
        <EditTodoForm
          _id={_id}
          description={description}
          toggleEditForm={toggle}
        />
      ) : (
        <>
          <Checkbox
            tabIndex={-1}
            checked={completed}
            onClick={() => {
              dispatch({ type: "TOGGLE", id: _id, completed: completed });
            }}
          />
          <ListItemText
            style={{ textDecoration: completed ? "line-through" : "none" }}
          >
            {description}
            <Typography variant="subtitle2">
              {lastUpdated}: {formatedDate()}
            </Typography>
          </ListItemText>
          <ListItemSecondaryAction>
            <IconButton
              aria-label="Delete"
              onClick={() => {
                dispatch({ type: "REMOVE", id: _id });
                handleDeleteClick();
              }}
            >
              <DeleteIcon></DeleteIcon>
            </IconButton>
            <IconButton aria-label="Edit" onClick={toggle}>
              <EditIcon></EditIcon>
            </IconButton>
          </ListItemSecondaryAction>{" "}
        </>
      )}
    </ListItem>
  );
}

export default memo(Todo);
