import uuid from "uuid/v4";
import axios from "axios";
import Router from "next/router";

import { Cookies } from "react-cookie";

const cookies = new Cookies();
const token = cookies.get("token");

const todoReducer = async (state, action) => {
  switch (action.type) {
    case "ADD":
      const description = action.task;
      const body = { description };
      try {
        const proxyUrl = "https://cors-anywhere.herokuapp.com/";
        const targetUrl = "https://owusu-task-manager.herokuapp.com/tasks";
        const res = await axios({
          url: proxyUrl + targetUrl,
          method: "post",
          data: body,
          headers: {
            Authorization: "Bearer " + token
          }
        });
        const newTask = res.data;
        return [
          // ...state,
          {
            _id: newTask._id,
            description: action.task,
            completed: false
          }
        ];
      } catch (e) {
        console.log(e);
      }

    case "REMOVE":
      try {
        const proxyUrl = "https://cors-anywhere.herokuapp.com/";
        const targetUrl = `https://owusu-task-manager.herokuapp.com/tasks/${action.id}`;
        const res = await axios({
          url: proxyUrl + targetUrl,
          method: "delete",
          headers: {
            Authorization: "Bearer " + token
          }
        });
        Router.push("/TodoAppPage");
        console.log("deleted: ", res.data.description);
      } catch (e) {
        console.log(e);
      }
    case "TOGGLE":
      try {
        const proxyUrl = "https://cors-anywhere.herokuapp.com/";
        const targetUrl = `https://owusu-task-manager.herokuapp.com/tasks/${action.id}`;
        var isDone = !action.completed;
        var updateData = { completed: isDone };
        const res = await axios({
          url: proxyUrl + targetUrl,
          method: "patch",
          data: updateData,
          headers: {
            Authorization: "Bearer " + token
          }
        });
        Router.push("/TodoAppPage");
      } catch (e) {
        console.log(e);
      }
      return state.map(todo =>
        todo.id === action.id ? { ...todo, completed: !todo.completed } : todo
      );
    // case "EDIT":
    //   return state.map(todo =>
    //     todo.id === action.id ? { ...todo, task: action.newTask } : todo
    //   );
    default:
      console.log("case not found in reducer");
      return state;
  }
};

export default todoReducer;

// {type: 'ADD', task: "Walk the Dog"}
