import React, { useContext } from 'react';
import axios from 'axios';
import TodoList from '../components/TodoList';
import TodoForm from '../components/TodoForm';
// import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';
// import AppBar from '@material-ui/core/AppBar';
// import { Toolbar } from '@material-ui/core';
import Grid from '@material-ui/core/Grid';
import { UserContext, UserProvider } from '../components/contexts/user.context';
import { TodosProvider } from '../components/contexts/todos.context';

var token;

function TodoAppPage() {
  token = useContext(UserContext);
  console.log('todoapp page token: ', token);

  return (
    <Paper
      style={{
        paddding: 0,
        margin: 0,
        height: '100vh',
        backgroundColor: '#fafafa'
      }}
      elevation={0}
    >
      {/* <AppBar color="primary" position="static" style={{ height: '64px' }}>
        <Toolbar>
          <Typography color="inherit">TODOS WITH HOOKS</Typography>
        </Toolbar>
      </AppBar> */}
      <Grid container justify="center" style={{ marginTop: '1rem' }}>
        <Grid item xs={11} md={8} lg={4}>
          <UserProvider value={token}>
            <TodosProvider>
              <TodoForm />
              <TodoList />
            </TodosProvider>
          </UserProvider>
        </Grid>
      </Grid>
    </Paper>
  );
}

TodoAppPage.getInitialProps = async () => {
  console.log('token inside getinit:', token);
  const proxyUrl = 'https://cors-anywhere.herokuapp.com/';
  const targetUrl = 'https://owusu-task-manager.herokuapp.com/tasks';

  try {
    const res = await axios.get(proxyUrl + targetUrl, {
      headers: {
        Authorization: 'Bearer ' + token.token
      }
    });
    const { data } = res; // const data = res.data
    console.log(data);
    return {
      posts: data
    };
  } catch (e) {
    console.error('Error:', e);
  }
};

export default TodoAppPage;
