          url: proxyUrl + targetUrl,
          method: "post",
          data: body,
          headers: {
            Authorization: "Bearer " + token
          }
        });
        const data = res.data;
        newTasks = [
          ...state,
          {
            _id: data._id,
            description: data.description,
            completed: false,
            updatedAt: data.updatedAt
          }
        ];
        Router.push("/TodoAppPage");
        return newTasks;
        break;
      } catch (e) {
        console.log(e);
      }

