import axios from 'axios';
const Post = props => <h1>Looking at post #{props.id}</h1>;

Post.getInitialProps = async ({ query }) => {
  const res = await axios.get(
    'https://jsonplaceholder.typicode.com/comments?postId=1'
  );
  const { data } = res;
  console.log(data[0]);
  return query;
};

export default Post;
