import React, { useContext } from "react";
import Router from "next/router";
import TextField from "@material-ui/core/TextField";
import Paper from "@material-ui/core/Paper";
import useInputState from "./hooks/useInputState";
import { DispatchContext } from "./contexts/todos.context";
import { LanguageContext } from "./contexts/LanguageContext";
import { withStyles } from "@material-ui/core/styles";
import { createMuiTheme } from "@material-ui/core/styles";
import { ThemeProvider } from "@material-ui/styles";
import { SnackbarProvider, useSnackbar } from "notistack";

const theme = createMuiTheme({
  overrides: {
    MuiInputLabel: {
      root: {
        height: 20,
        color: "orange",
        "&$focused": {
          color: "blue"
        }
      }
    },
    MuiOutlinedInput: {
      input: {
        padding: "23px"
      }
    }
  }
});

const words = {
  en: {
    addTodo: "Add New Todo",
    lastUpdated: "Last Update: "
  },
  fr: {
    addTodo: "Ajouter un nouveau Todo",
    lastUpdated: "Dernières mises à jour: "
  },
  es: {
    addTodo: "Añadir nuevo Todo",
    lastUpdated: "Últimas actualizaciones: "
  },
  de: {
    addTodo: "Neuen Todo hinzufügen",
    lastUpdated: "Letzte Aktualisierung: "
  },

  ja: { addTodo: "新しいTodoを追加", lastUpdated: "最新の更新: " }
};

function TodoForm(props) {
  const { classes } = props;
  const [value, handleChange, reset] = useInputState("");
  const dispatch = useContext(DispatchContext);
  const { language } = useContext(LanguageContext);
  const { addTodo } = words[language];
  const { enqueueSnackbar, closeSnackbar } = useSnackbar();

  const handleAddClick = () => {
    const message = `New Task: "${value}"`;
    enqueueSnackbar(message, {
      variant: "success"
    });
  };

  return (
    <Paper elevation={3} style={{ margin: "1rem 0", padding: "0" }}>
      <form
        onSubmit={e => {
          e.preventDefault();
          handleAddClick();
          dispatch({ type: "ADD", task: value });
          reset();
          Router.push("/TodoAppPage");
        }}
        style={{ height: "65px", padding: "0px 0px 20px 0px" }}
      >
        <ThemeProvider theme={theme}>
          <TextField
            id="outlined-basic"
            variant="outlined"
            value={value}
            onChange={handleChange}
            margin="normal"
            label={addTodo}
            fullWidth
          />
        </ThemeProvider>
      </form>
    </Paper>
  );
}

export default TodoForm;
