import React, { createContext, useEffect } from 'react';
import { useDBReducer } from '../hooks/useDBReducer';
import { useAuthReducer } from '../hooks/useAuthReducer';
import todoReducer from '../reducers/todo.reducer';
import authReducer from '../reducers/auth.reducer';

const defaultTodos = [
  { id: 1, task: 'Mow the lawn using goats', completed: false },
  { id: 2, task: 'Release lady bugs into garden', completed: true }
];
const defaultToken = { token: '', error: false };

export const UserContext = createContext();
export const TodosContext = createContext();
export const DispatchContext = createContext();

export function TodosProvider(props) {
  const [token, authenticate] = useAuthReducer(authReducer, defaultToken);
  useEffect(() => {
    const [todos, dispatch] = useDBReducer(token, defaultTodos, todoReducer);
  }, [token.token]);

  return (
    <TodosContext.Provider value={todos}>
      <DispatchContext.Provider value={dispatch}>
        <UserContext.Provider value={{ token, authenticate }}>
          {props.children}
        </UserContext.Provider>
      </DispatchContext.Provider>
    </TodosContext.Provider>
  );
}
