import React, { useContext } from 'react';
import Paper from '@material-ui/core/Paper';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import Todo from './Todo';
import { TodosContext } from './contexts/todos.context';
import { UserContext } from './contexts/user.context';

function TodoList({ posts }) {
  const todos = useContext(TodosContext);

  if (todos.length)
    return (
      <>
        <Paper>
          <List>
            {todos.map((todo, i) => (
              // To add a key to a fragment, we have to use the long-hand version
              <React.Fragment key={i}>
                <Todo {...todo} key={todo._id} />
                {i < todos.length - 1 && <Divider />}
              </React.Fragment>
            ))}
          </List>
        </Paper>
        <Paper>
          <h1>{console.log(posts)} </h1>
        </Paper>
      </>
    );
  return null;
}

TodoList.getInitialProps = async () => {
  const token = useContext(UserContext);
  const proxyUrl = 'https://cors-anywhere.herokuapp.com/';
  const targetUrl = 'https://owusu-task-manager.herokuapp.com/tasks';

  try {
    const res = await axios.get(proxyUrl + targetUrl, {
      headers: {
        Authorization: 'Bearer ' + token.token
      }
    });
    const { data } = res; // const data = res.data

    return {
      posts: data
    };
  } catch (e) {
    console.error('Error:', e);
  }
};
