import React, { Component, useContext } from 'react';
import Router from 'next/router';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import Snackbar from '@material-ui/core/Snackbar';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import CssBaseline from '@material-ui/core/CssBaseline';
import FormControl from '@material-ui/core/FormControl';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import MenuItem from '@material-ui/core/MenuItem';
import Select from '@material-ui/core/Select';
import withStyles from '@material-ui/core/styles/withStyles';
import styles from './styles/FormStyles';
import cubeSpinner from './styles/cubeSpinner';
import { LanguageContext } from './contexts/LanguageContext';

const words = {
  english: {
    signIn: 'Sign In',
    emailLang: 'Email Address',
    passwordLang: 'Password',
    remember: 'Remember Me'
  },
  french: {
    signIn: 'Se Connecter',
    emailLang: 'Adresse Électronique',
    passwordLang: 'Mot de Passe',
    remember: 'Souviens-toi De Moi'
  },
  spanish: {
    signIn: 'Registrarse',
    emailLang: 'Correo Electrónico',
    passwordLang: 'Contraseña',
    remember: 'Recuérdame'
  },
  german: {
    signIn: 'anmelden',
    emailLang: 'E-Mail-Addresse',
    passwordLang: 'Passwort',
    remember: 'Behalte mich in Erinnerung'
  },

  日本語: {
    signIn: 'サインイン',
    emailLang: '電子メールアドレス',
    passwordLang: 'パスワード',
    remember: '私を覚えている'
  }
};

class Form extends Component {
  static contextType = LanguageContext;
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      password: '',
      open: false,
      snackMessage: '',
      loading: false
    };
    this.closeSnackbar = this.closeSnackbar.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  async handleSubmit(evt) {
    evt.preventDefault();

    this.setState(() => ({ loading: true }));

    try {
      const proxyUrl = 'https://cors-anywhere.herokuapp.com/';
      const url = 'https://owusu-task-manager.herokuapp.com/users/login';
      const response = await fetch(proxyUrl + url, {
        method: 'POST',
        body: JSON.stringify(this.state),
        headers: {
          'Content-Type': 'application/json'
        }
      });

      const userInfo = await response.json();
      window.global = userInfo;

      console.log(window.global);
      this.setState(() => ({ loading: false, token: userInfo.token }));

      console.log(userInfo);
      Router.push('/TodoAppPage');
    } catch (error) {
      console.error('Error:', error);
      Router.push('/');
      this.setState({
        loading: false,
        open: true,
        snackMessage: 'Email/Password incorrect'
      });
    }
    this.setState({ email: '', password: '' });
  }

  handleChange(evt) {
    this.setState({
      [evt.target.name]: evt.target.value
    });
  }

  closeSnackbar() {
    this.setState({ open: false });
  }

  render() {
    const { language, changeLanguage } = this.context;
    const { classes } = this.props;
    const { email, password, snackMessage, open, loading } = this.state;
    const { emailLang, signIn, passwordLang, remember } = words[language];

    if (loading) {
      return <div className={classes.loader}>{cubeSpinner()}</div>;
    }

    return (
      <main className={classes.main}>
        <Paper className={classes.paper}>
          <Avatar className={classes.avatar}>
            <LockOutlinedIcon />
          </Avatar>
          <Typography variant="h5">{signIn}</Typography>
          <Select value={language} onChange={changeLanguage}>
            <MenuItem value="english">English</MenuItem>
            <MenuItem value="french">French</MenuItem>
            <MenuItem value="spanish">Spanish</MenuItem>
            <MenuItem value="german">German</MenuItem>
            <MenuItem value="日本語">日本語</MenuItem>
          </Select>
          <form className={classes.form} onSubmit={this.handleSubmit}>
            <FormControl margin="normal" required fullWidth>
              <InputLabel htmlFor="email">{emailLang}</InputLabel>
              <Input
                id="email"
                name="email"
                value={email}
                onChange={this.handleChange}
                autoFocus
              ></Input>
            </FormControl>
            <FormControl margin="normal" required fullWidth>
              <InputLabel htmlFor="password">{passwordLang}</InputLabel>
              <Input
                id="password"
                name="password"
                value={password}
                onChange={this.handleChange}
                autoFocus
              ></Input>
            </FormControl>
            <FormControlLabel
              control={<Checkbox color="primary" />}
              label={remember}
            />
            <Button
              variant="contained"
              type="submit"
              fullWidth
              color="primary"
              className={classes.submit}
            >
              {signIn}
            </Button>
          </form>
        </Paper>
        <Snackbar
          anchorOrigin={{ vertical: 'bottom', horizontal: 'left' }}
          open={open}
          autoHideDuration={3000}
          message={<span id="mesage-id">{snackMessage} </span>}
          ContentProps={{ 'aria-describedby': 'message-id' }}
          onClose={this.closeSnackbar}
          variant="error"
          action={[
            <IconButton
              onClick={this.closeSnackbar}
              color="inherit"
              key="close"
              aria-label="close"
            >
              <CloseIcon />
            </IconButton>
          ]}
        />
      </main>
    );
  }
}

export default withStyles(styles)(Form);
