import { useReducer, useEffect } from 'react';
import $ from 'jquery';

//token , starter tasks?, reducer
function useDBReducer(token, defaultVal, todoReducer) {
  console.log('token:', token);
  //make piece of state, based off of value in localstorage (or default)
  //in case of a DB the key will equal the collection name
  const [state, dispatch] = useReducer(todoReducer, defaultVal, () => {
    //this function checks database for existing tasks for the user
    let val;
    try {
      const proxyUrl = 'https://cors-anywhere.herokuapp.com/';
      const targetUrl = 'https://owusu-task-manager.herokuapp.com/tasks';

      $.ajax({
        url: proxyUrl + targetUrl,
        type: 'GET',
        dataType: 'json',
        beforeSend: setHeader,
        success: function(res) {
          console.log(res);
        }
      });
      function setHeader(xhr) {
        xhr.setRequestHeader('Authorization', 'Bearer ' + token);
      }
    } catch (e) {
      console.error('Error:', e);
      val = defaultVal;
    }
    return val;
  });

  return [state, dispatch];
}

export { useDBReducer };

