import React, { useContext } from "react";
import useInputState from "./hooks/useInputState";
import TextField from "@material-ui/core/TextField";
import { DispatchContext } from "./contexts/todos.context";
import { useSnackbar } from "notistack";

function EditTodoForm({ _id, description, toggleEditForm, sorted }) {
  const dispatch = useContext(DispatchContext);
  const [value, handleChange, reset] = useInputState(description);
  const { enqueueSnackbar } = useSnackbar();

  const handleEditClick = () => {
    enqueueSnackbar(`✏️ Edited Task: "${description}"`);
  };

  return (
    <form
      onSubmit={e => {
        e.preventDefault();
        handleEditClick();
        dispatch({ type: "EDIT", id: _id, newTask: value });
        sorted({ type: "EDIT", id: _id, todo: value });
        reset();
        toggleEditForm();
      }}
      style={{ marginLeft: "1rem", width: "75%" }}
    >
      <TextField
        margin="normal"
        value={value}
        onChange={handleChange}
        fullWidth
        autoFocus
      />
    </form>
  );
}
export default EditTodoForm;
