import React, { useContext } from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import FormControl from '@material-ui/core/FormControl';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import MenuItem from '@material-ui/core/MenuItem';
import Select from '@material-ui/core/Select';
import Snackbar from '@material-ui/core/Snackbar';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import withStyles from '@material-ui/core/styles/withStyles';
import styles from './styles/FormStyles';
import { LanguageContext } from './contexts/LanguageContext';
import { ThemeContext } from './contexts/ThemeContext';
import { DispatchContext } from './contexts/todos.context';
import useInputState from './hooks/useInputState';
import cubeSpinner from './styles/cubeSpinner';

const words = {
  english: {
    signIn: 'Sign In',
    email: 'Email Address',
    password: 'Password',
    remember: 'Remember Me'
  },
  french: {
    signIn: 'Se Connecter',
    email: 'Adresse Électronique',
    password: 'Mot de Passe',
    remember: 'Souviens-toi De Moi'
  },
  spanish: {
    signIn: 'Registrarse',
    email: 'Correo Electrónico',
    password: 'Contraseña',
    remember: 'Recuérdame'
  },
  german: {
    signIn: 'anmelden',
    email: 'E-Mail-Addresse',
    password: 'Passwort',
    remember: 'Behalte mich in Erinnerung'
  },

  日本語: {
    signIn: 'サインイン',
    email: '電子メールアドレス',
    password: 'パスワード',
    remember: '私を覚えている'
  }
};

function Form(props) {
  const { language, changeLanguage, token } = useContext(LanguageContext);
  const dispatch = useContext(DispatchContext);
  const { classes } = props;
  const { email, signIn, password, remember } = words[language];
  const [emailVal, handleEmailChange, emailReset] = useInputState('');
  const [passVal, handlePassChange, passReset] = useInputState('');
  const [open, setSnackOpen] = React.useState(false);
  const [loading, setLoader] = React.useState(false);

  const handleClick = () => {
    setSnackOpen(true);
  };

  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }

    setSnackOpen(false);
  };

  const handleSubmit = async evt => {
    evt.preventDefault();
    //toggle loader
    setLoader(true);

    try {
      const proxyUrl = 'https://cors-anywhere.herokuapp.com/';
      const url = 'https://owusu-task-manager.herokuapp.com/users/login';
      const response = await fetch(proxyUrl + url, {
        method: 'POST',
        body: JSON.stringify(this.state),
        headers: {
          'Content-Type': 'application/json'
        }
      });

      const userInfo = await response.json();
      token = userInfo.token;
      setLoader(false);
      // this.setState(() => ({ loading: false, token: userInfo.token }));

      console.log(userInfo);
      // Router.push('/TodoAppPage');
    } catch (error) {
      console.error('Error:', error);
      Router.push('/');
      setLoader(false);
      setSnackOpen(true);

      // this.setState({ email: '', password: '' });
    }
  };

  if (loading) {
    return <div className={classes.loader}>{cubeSpinner()}</div>;
  }

  return (
    <main className={classes.main}>
      <Paper className={classes.paper}>
        <Avatar className={classes.avatar}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography variant="h5">{signIn}</Typography>
        <Select value={language} onChange={changeLanguage}>
          <MenuItem value="english">English</MenuItem>
          <MenuItem value="french">French</MenuItem>
          <MenuItem value="spanish">Spanish</MenuItem>
          <MenuItem value="german">German</MenuItem>
          <MenuItem value="日本語">日本語</MenuItem>
        </Select>
        <form
          className={classes.form}
          onSubmit={e => {
            e.preventDefault();
            dispatch({ type: 'LOGIN', email: emailVal, password: passVal }); //fix this later
            emailReset();
            passReset();
          }}
        >
          <FormControl margin="normal" required fullWidth>
            <InputLabel htmlFor="email">{email}</InputLabel>
            <Input
              id="email"
              name="email"
              value={emailVal}
              onChange={handleEmailChange}
              autoFocus
            ></Input>
          </FormControl>
          <FormControl margin="normal" required fullWidth>
            <InputLabel htmlFor="password">{password}</InputLabel>
            <Input
              id="password"
              name="password"
              value={passVal}
              onChange={handlePassChange}
              autoFocus
            ></Input>
          </FormControl>
          <FormControlLabel
            control={<Checkbox color="primary" />}
            label={remember}
          />
          <Button
            variant="contained"
            type="submit"
            fullWidth
            color="primary"
            className={classes.submit}
          >
            {signIn}
          </Button>
        </form>
      </Paper>
      <Button onClick={handleClick}>Open simple snackbar</Button>
      <Snackbar
        anchorOrigin={{
          vertical: 'bottom',
          horizontal: 'left'
        }}
        open={open}
        autoHideDuration={3000}
        onClose={handleClose}
        ContentProps={{
          'aria-describedby': 'message-id'
        }}
        message={
          <span id="message-id">Email and/or Password is incorrect</span>
        }
        action={[
          <IconButton
            key="close"
            aria-label="close"
            color="inherit"
            className={classes.close}
            onClick={handleClose}
          >
            <CloseIcon />
          </IconButton>
        ]}
      />
    </main>
  );
}

export default withStyles(styles)(Form);
