import React, { useContext } from "react";
import TodoList from "../components/TodoList";
import TodoForm from "../components/TodoForm";
// import Typography from '@material-ui/core/Typography';
import Paper from "@material-ui/core/Paper";
import { handleAuthSSR } from "../components/handleAuthSSR";
import Grid from "@material-ui/core/Grid";
// import { UserContext, UserProvider } from "../components/contexts/user.context";
import { TodosProvider } from "../components/contexts/todos.context";

function TodoAppPage(props) {
  return (
    <Paper
      style={{
        paddding: 0,
        margin: 0,
        height: "100vh",
        backgroundColor: "#fafafa"
      }}
      elevation={0}
    >
      <Grid container justify="center" style={{ marginTop: "1rem" }}>
        <Grid item xs={11} md={8} lg={4}>
          <TodosProvider>
            <TodoForm />
            <Paper>
              <TodoList {...props} />
            </Paper>
          </TodosProvider>
        </Grid>
      </Grid>
    </Paper>
  );
}

TodoAppPage.getInitialProps = async ctx => {
  const res = await handleAuthSSR(ctx);
  const { data } = res; // const data = res.data

  return {
    posts: data
  };
};

export default TodoAppPage;
