import React, { Component } from "react";
import DraggableTodoList from "./DraggableTodoList";
import { withSortedContext } from "./contexts/SortedContext";
import { TodosContext } from "./contexts/todos.context";
import { Cookies } from "react-cookie";
const cookies = new Cookies();

class TodoList extends Component {
  static contextType = TodosContext;

  constructor(props) {
    super(props);
    this.state = {
      tasks: cookies.get("tasks") || props.posts,
      prevArr: [],
      sorted: false
    };
  }

  onSortEnd = ({ oldIndex, newIndex }) => {
    console.log("before sort: ", this.state.prevArr);
    if (this.state.prevArr.length === 0) {
      // var arr = this.props.posts;
      var arr = cookies.get("tasks");
      //if prevArr is same size as state.tasks change nothing
    } else if (this.state.prevArr.length === this.state.tasks.length) {
      var arr = this.state.prevArr; //when a new task is added ..it's not reflected in prevArr...duh!

      //if prev arr is smaller than state.tasks push the new element to the end
    } else if (this.state.prevArr.length < this.state.tasks.length) {
      this.setState({
        prevArr: [
          ...this.state.prevArr,
          this.state.tasks[this.state.tasks.length - 1]
        ]
      });
      var arr = this.state.prevArr;

      //if prev arr is larger than state.tasks filter out the corresponding element
    } else if (this.state.prevArr.length > this.state.tasks.length) {
      this.setState({
        prevArr: this.state.prevArr.filter(
          task => task._id !== this.props.sortedContext._id
        )
      });
      var arr = this.state.prevArr;
    } else {
      return console.log("something did not go quite as planned...");
    }

    arr = arr.slice();
    arr.splice(
      newIndex < 0 ? arr.length + newIndex : newIndex,
      0,
      arr.splice(oldIndex, 1)[0]
    );
    console.log("after sort: ", arr);
    this.setState({
      tasks: arr,
      prevArr: arr
      // set a cookie here
    });
    // cookies.set("tasks", this.state.tasks);
  };

  componentDidUpdate(prevState) {
    if (prevState.tasks !== this.state.tasks) {
      cookies.set("tasks", this.state.tasks);
    }
  }

  static getDerivedStateFromProps(props, state) {
    // if (!state.sorted) {
    //if state.sorted is false

    //need the id here
    const { type, id, todo } = props.sortedContext.type;
    const { tasks } = state;

    switch (type) {
      case "ADD":
        return {
          tasks: [...tasks, { _id: id, description: todo, completed: false }]
        };
        break;
      case "REMOVE":
        return { tasks: tasks.filter(task => task._id !== id) };
      case "TOGGLE":
        return {
          tasks: tasks.map(task =>
            task._id === id ? { ...task, completed: !task.completed } : task
          )
        };
        break;
      case "EDIT":
        return {
          tasks: tasks.map(task =>
            task._id === id ? { ...task, description: todo } : task
          )
        };
        break;
      default:
        return { tasks };
    }
    // } else return null;
  }

  render() {
    // console.log("this.state.sorted: ", this.state.sorted);
    const { resetSort } = this.props.sortedContext;
    const { tasks } = this.state;
    if (tasks.length)
      return (
        <DraggableTodoList
          axis="y"
          distance={20}
          onSortEnd={this.onSortEnd}
          posts={tasks}
          sorted={resetSort}
        ></DraggableTodoList>
      );
    return null;
  }
}

export default withSortedContext(TodoList);
