import React, { useContext, useEffect, useState, Component } from "react";
import Paper from "@material-ui/core/Paper";
import List from "@material-ui/core/List";
import Divider from "@material-ui/core/Divider";
import DraggableTodoList from "./DraggableTodoList";
import { arrayMove } from "array-move";
import { TodosContext } from "../components/contexts/todos.context";
import { TodosProvider } from "../components/contexts/todos.context";

class TodoList extends Component {
  static contextType = TodosContext;

  constructor(props) {
    super(props);
    this.state = {
      tasks: props.posts
    };
    // this.onSortEnd = this.onSortEnd.bind(this);
  }

  onSortEnd = ({ oldIndex, newIndex }) => {
    this.setState({
      tasks: arrayMove(this.state.tasks, oldIndex, newIndex)
    });
  };

  componentDidUpdate(prevProps) {
    if (this.props.posts !== prevProps.posts)
      this.setState({
        tasks: this.context
      });
  }

  // componentWillReceiveProps(nextProps) {
  //   this.setState({
  //     tasks: nextProps.posts
  //   });
  // }

  render() {
    console.log("this.state.tasks: ", this.state.tasks);
    console.log("this.context: ", this.context);
    const { tasks } = this.state;

    // console.log("1:", props.post);
    if (tasks.length)
      return (
        <DraggableTodoList
          axis="y"
          distance={20}
          onSortEnd={this.onSortEnd}
          posts={tasks}
        ></DraggableTodoList>
      );
    return null;
  }
}

export default TodoList;

