import React, { Component } from "react";

import DraggableTodoList from "./DraggableTodoList";
import { TodosContext } from "../components/contexts/todos.context";
import { SortedContext, withSortedContext } from "./contexts/SortedContext";

class TodoList extends Component {
  // static contextType = TodosContext;

  constructor(props) {
    super(props);
    this.state = {
      tasks: props.posts,
      sorted: false
    };
    // this.sorted = this.sorted.bind(this);
  }

  onSortEnd = ({ oldIndex, newIndex }) => {
    console.log("before sort: ", this.props.posts);
    let arr = this.props.posts;
    arr = arr.slice();
    arr.splice(
      newIndex < 0 ? arr.length + newIndex : newIndex,
      0,
      arr.splice(oldIndex, 1)[0]
    );
    console.log("after sort: ", arr);
    this.setState({
      tasks: arr,
      sorted: true
    });
    this.props.sortedContext.isSorted = false;

    console.log("this.state.tasks: ", this.state.tasks);
  };

  // sorted = () => {
  //   console.log("running sorted function!");
  //   this.setState({
  //     sorted: !this.props.sortedContext.isSorted
  //   });
  // };

  static getDerivedStateFromProps(props, state) {
    //consider component did update
    // const { isSorted } = props.sortedContext;
    console.log("state.sorted in GDSFP: ", state.sorted);
    console.log("props.sortedContext: ", props);
    if (!state.sorted || props.sortedContext.isSorted) {
      //check if props length is different from state length [add/delete]
      if (props.posts.length !== state.tasks.length) {
        // console.log("1st condition met");
        //update state to match props
        return { tasks: props.posts };
        //check if state length is equal to posts length
      } /*else if (props.posts.length === state.tasks.length) {*/
      // console.log("2nd condition met");
      //loop through each task in state
      for (let i = 0; i < state.tasks.length; i++) {
        //if props tasks equal state tasks completion, continue to next element
        if (props.posts[i].completed === !state.tasks[i].completed) {
          //if there is inequality of props vs. state completion, update state to match props
          return { tasks: props.posts };
        }
        if (props.posts[i].description !== state.tasks[i].description) {
          return { tasks: props.posts };
        }
      }
      /*} else {*/
      return null;
    } else return null;
  }

  render() {
    console.log("this.state.sorted: ", this.state.sorted);
    const { posts, resetSort } = this.props.sortedContext;
    const { tasks } = this.state;
    if (tasks.length)
      return (
        <DraggableTodoList
          axis="y"
          distance={20}
          onSortEnd={this.onSortEnd}
          posts={tasks}
          sorted={resetSort} //instead of sorted
        ></DraggableTodoList>
      );
    return null;
  }
}

export default withSortedContext(TodoList);
// TodoList.contextType = SortedContext;
